$(function(){sportCarousel.init();$(".menu-toggle").click(function(){$("#site_wrapper").toggleClass("show-nav")});$("body").bind("touchmove",function(a){$("#site_wrapper").hasClass("show-nav")&&a.preventDefault()});$(".sport-carousel").click(function(a){var b=$(this).position().left;a.pageX-b>=$(this).width()/2?sportCarousel.showNext():sportCarousel.showPrevious()});$(".icon-holder").click(function(){var a=parseInt(this.id.substring(10));sportCarousel.selectSlide(a);sportCarousel.showCarousel()});
    $("body").keyup(function(a){27===a.keyCode&&sportCarousel.hideCarousel()});$(".next-arrow").click(function(){sportCarousel.showNext()});$(".prev-arrow").click(function(){sportCarousel.showPrevious()});$("#exit-button").click(function(){sportCarousel.hideCarousel()});$(".current-page #home-nav-icon").click(function(){$("body").hasClass("carousel-open")&&sportCarousel.hideCarousel()})});